import { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
//import { Link, useNavigate } from "react-router-dom";
import styles from "./styles.module.css";

const RegtopUpdate = () => {
    // const [data, setData] = useState({

    //     groupId:"",
	// 	groupName: "",
	// 	topic: "",
	// 	year: "",
	// 	supervisor: "",
    //     coSupervisor:"",
    //     //status:"N/A",
	// });
    // const [error, setError] = useState("");
	// const navigate = useNavigate();

	// const handleChange = ({ currentTarget: input }) => {
	// 	setData({ ...data, [input.name]: input.value });
	// };

    // const handleSubmit = async (e) => {
	// 	e.preventDefault();
	// 	try {
	// 		const url = "http://localhost:8082/api/topic"; 
	// 		const { data: res } = await axios.post(url, data);
	// 		navigate("/regtopicretrieve");
	// 		console.log(res.message);
	// 	} catch (error) {
	// 		if (
	// 			error.response &&
	// 			error.response.status >= 400 &&
	// 			error.response.status <= 500
	// 		) {
	// 			setError(error.response.data.message);
	// 		}
	// 	}
	// };

    const[groupId,setId] = useState()
    const[groupName,setGROUPNAME] = useState()
    const[topic,setTOPIC] = useState()
    const[year,setYEAR] = useState()
    const[supervisor,setSUPERVISOR] = useState()
    const[coSupervisor,setCOSUPERVISOR] = useState()
    //const[status,setSTATUS] = useState()

    useEffect(() => {

        axios.get("http://localhost:8082/api/topic/getGroupId/" +localStorage.getItem("groupId"))

        .then((response) =>{                   //set the details
            setId(response.data[0].groupId)
            setGROUPNAME(response.data[0].groupName)
            setTOPIC(response.data[0].topic)
            setYEAR(response.data[0].year)
            setSUPERVISOR(response.data[0].supervisor)
            setCOSUPERVISOR(response.data[0].coSupervisor)
            //setSTATUS(response.data[0].status)

            console.log(response.data)
        })

        .catch(function(error){

            console.log(error)
        });
    }, []);

    

    return(

    <div className={styles.topic_container}>
    <div className={styles.topic_form_container}>
        <div className={styles.colt}>
            {/* <h1>Group Registration</h1>
            <Link to="/reggroup">
                <button type="button" className={styles.btnw}>
                    Group Register
                </button>
            </Link> */}
        </div>
        <div className={styles.colt2}>
            <form className={styles.form_container} onSubmit={handleSubmit}>
                <h1>Update You Details</h1>
                <input
                    type="text"
                    placeholder="Group Id"
                    name="groupId"
                    onChange={handleChange}
                    value={data.groupId}
                    required
                    readOnly
                    className={styles.input}
                />
                <input
                    type="text"
                    placeholder="Group Name"
                    name="groupName"
                    onChange={handleChange}
                    value={data.groupName}
                    required
                    className={styles.input}
                />
                <input
                    type="text"
                    placeholder="Topic"
                    name="topic"
                    onChange={handleChange}
                    value={data.topic}
                    required
                    className={styles.input}
                />
                <input
                   type = "text"
                   placeholder="Year"
                   name="year"
                   onChange={handleChange}
                   value = {data.year}
                   required
                   className= {styles.input}
                />
                <input
                    type="text"
                    placeholder="Supervisor Name"
                    name="supervisor"
                    onChange={handleChange}
                    value={data.supervisor}
                    required
                    className={styles.input}
                />
                 <input
                    type="text"
                    placeholder="Co Supervisor Name"
                    name="coSupervisor"
                    onChange={handleChange}
                    value={data.coSupervisor}
                    required
                    className={styles.input}
                />
                {/* <input
                    type="text"
                    placeholder="Panal member's feedback"
                    name="status"
                    onChange={handleChange}
                    value={data.status}
                    className={styles.input}
                /> */}
                 {error && <div className={styles.error}>{error}</div>}
                <button type="submit" className={styles.btnb}>
                    Update
                </button>
            </form>
        </div>
    </div>
    </div>

    );

};

export default RegtopUpdate;
